$(document).ready(function () {
    $('.lang').click(function () {
        $('.lang-list').toggleClass('active');
    });
    var width = $('#slider-container').width();
    $('.slides>li').width(width);
    $('.slides').width(width*$('.slides>li').length);
    // position
    $('.slides').css('left',-width);
    $('.slides>li:last-child').prependTo('.slides');
    //move slides forward
    function nextSlide(){
        $('.slides').animate({
            'margin-left':-width
        }, 500, function () {
            $('.slides>li:first-child').appendTo('.slides');
        $('.slides').css('margin-left', 0);
        });
    }
    //move slides backwards
    function prevSlide(){
        $('.slides').animate({
            'margin-left':width
        }, 500, function () {
            $('.slides>li:last-child').prependTo('.slides');
            $('.slides').css('margin-left', 0);
        });
    }

    //controls
    $('.next').click(nextSlide);
    $('.prev').click(prevSlide);


        $('.business-list-items').hover(function(e) {
            var data = $(this).data('attr'),
                $el = $('.business-card-item[data-attr="'+data+'"]');
            if($el.length) {
                $el.prevAll().addClass('mrgn-top');
            }
        }, function(e){

            var data = $(this).data('attr'),
                $el = $('.business-card-item[data-attr="'+data+'"]');
            if($el.length) {
                $el.prevAll().removeClass('mrgn-top');
            }
        });

        $('.icon-show-menu').on('click', function (){
            $('.nav-toolbar-container').toggleClass('menu-mb');
    });
});